function parseQuizJson(rawText) {
  if (!rawText) {
    throw new Error("입력 값이 비어 있습니다.");
  }

  // 앞뒤 공백 및 줄바꿈 정리
  const cleaned = rawText.trim();

  let parsed;
  try {
    parsed = JSON.parse(cleaned);
  } catch (e) {
    throw new Error("JSON 파싱 실패: " + e.message);
  }

  return parsed;
}

