const input = $input.first().json;

if (!Array.isArray(input.questions)) {
  throw new Error("questions 배열이 존재하지 않습니다.");
}

if (!Array.isArray(input.answers)) {
  throw new Error("answers 배열이 존재하지 않습니다.");
}

// answers를 id 기준으로 맵핑
const answerMap = {};
for (const a of input.answers) {
  answerMap[a.id] = {
    answer: a.answer,
    explanation: a.explanation
  };
}

// questions에 answer 병합
const mergedQuestions = input.questions.map(q => ({
  ...q,
  answer: answerMap[q.id]?.answer ?? null,
  explanation: answerMap[q.id]?.explanation ?? null
}));

// questions 배열만 아이템으로 반환
return mergedQuestions.map(q => ({
  json: q
}));
