// Loop over input items and add a new field called 'myNewField' to the JSON of each one
for (const item of $input.all()) {
  const text = item.json.data
  .replace(/<script[\s\S]*?>[\s\S]*?<\/script>/gi, '')
  .replace(/<style[\s\S]*?>[\s\S]*?<\/style>/gi, '')
  .replace(/<[^>]*>/g, '')
  .trim();
  item.json.data = text;
}

return $input.all();

function generateEmbedding(){
  const title = "title: " + $input.first().json.title;
  const content = "content: " + $input.first().json.content;
  const url = "url: " + $input.first().json.url;

  return title + "\n" + content + "\n" + url + "\n";
}

return [{
  embedding: generateEmbedding()
}];

// 섹션별 분리
function extract(label, text) {
  const regex = new RegExp(`${label}[\\s\\S]*?(?=\\n\\d+\\. |$)`, "g");
  const match = text.match(regex);
  if (!match) return "";
  return match[0].replace(label, "").trim();
}

for (const item of $input.all()) {
   // 섹션 추출
  const raw = item.json.output;
  const questionType = extract("1. 질문 유형",raw);
  const generalAnswer = extract("2. 답변",raw);
  const category = extract("3. 학습 카테고리",raw);
  const schedule = extract("4. 일정",raw);
  const lectures = extract("5. 수업 자료",raw);
  const blogs = extract("6. 블로그 자료",raw);

  // 현재 시간
  const now = new Date().toISOString();

  return {
      질문유형: questionType,
      학습카테고리: category,
      답변: generalAnswer,
      일정: schedule,
      수업자료: lectures,
      블로그자료: blogs,
      생성일시: now
    }
}

return $input.all();

